mapper.select:
SELECT member_name AS name, member_storedphoto AS storedProfile, club_num AS clubNum
FROM member
WHERE club_num = #{value}

mapper.delete:
DELETE
FROM matchboard
WHERE club_num = #{clubNum}
  AND mb_type = #{type}

mapper.select:
SELECT mb_num, mb_name, mb_file, club_num, mb_type
FROM matchboard
WHERE club_num = #{clubNum}
  AND mb_type = #{type}
